function convertHashrate(hash) {
  if (!hash)
    return 0 + 'H/s';
  hash = parseInt(hash);
  if (hash > 900000000000)
    return (hash / 1000000000000).toFixed(2) + 'Ph/s';
  if (hash > 900000000)
    return (hash / 1000000000).toFixed(2) + 'Th/s';
  else if (hash > 900000)
    return (hash / 1000000).toFixed(2) + 'Gh/s';
  else if (hash > 900)
    return (hash / 1000).toFixed(2) + '';
  else
    return hash.toFixed(2) + 'Kh/s';
}
function convertWeight(net) {
    if (!net)
        return "N/A";
    if (net > 100000000000000){
        net = net / 100000000000000;
        unit = 'M';
    } else  if (net > 100000000000){
        net = net / 100000000000;
        unit = 'k';
    } else {
        net = net / 100000000;
        unit = '';
    }

    return parseFloat(net).toFixed(4) + ' ' + unit;
}
function convertExpectedtime(et) {
    if (!et)
        return "0.0 days";
    unit = 'minutes';
    interval = et / 60;
    hours = interval / 60;
    days = hours / 24;
    if (hours > 1) {
        interval = hours;
        unit = 'hours';
    }
    if (days > 1) {
        interval = days;
        unit = 'days';
    }
    return Math.round(interval) + ' ' + unit;
}
function convertMS(ms) {
  var d, h, m, s;
  s = Math.floor(ms / 1000);
  m = Math.floor(s / 60);
  s = s % 60;
  h = Math.floor(m / 60);
  m = m % 60;
  d = Math.floor(h / 24);
  h = h % 24;
  return {
    d: d,
    h: h,
    m: m,
    s: s
  };
}
